create table tarot_cards
(
    id                int auto_increment
        primary key,
    name              varchar(100) not null,
    number            int          null,
    arcana_type       varchar(50)  not null,
    suit              varchar(50)  null,
    archetype         varchar(255) null,
    attributes        text         null,
    symbolism         text         null,
    upright_meaning   text         null,
    reversed_meaning  text         null,
    upright_advice    text         null,
    reversed_advice   text         null,
    image_url         varchar(255) null,
    questions         text         null,
    astrological_sign varchar(50)  null,
    element           varchar(50)  null,
    chakra            varchar(50)  null,
    numerology        varchar(50)  null,
    keywords          text         null
);

