create table tarot_spreads
(
    id                bigint auto_increment
        primary key,
    name              varchar(255)                        not null,
    spread_type       enum ('basic', 'advanced')          not null,
    is_paid           bit       default b'0'              not null,
    is_popular        bit       default b'0'              not null,
    tags              varchar(255)                        null,
    test_count        bigint    default 0                 null,
    like_count        bigint    default 0                 null,
    image_large       varchar(255)                        null,
    image_small       varchar(255)                        null,
    description       text                                null,
    related_questions text                                null,
    created_at        timestamp default CURRENT_TIMESTAMP null,
    updated_at        timestamp default CURRENT_TIMESTAMP null on update CURRENT_TIMESTAMP
);

